osition":"AH07U01H9","zone":"AH07U01","position":"H9","is_shared":false,"places_shared":0,"price":72000,"price_shared":0},{"id":4821,"status_id":1008,"status":"disponible","full_position":"AH07U01H10","zone":"AH07U01","position":"H10","is_shared":false,"places_shared":0,"price":72000,"price_shared":0},{"id":4822,"status_id":1008,"status":"disponible","full_position":"AH07U01H11","zone":"AH07U01","position":"H11","is_shared":false,"places_shared":0,"price":72000,"price_shared":0},{"id":4823,"status_id":1008,"status":"disponible","full_position":"AH07U01H12","zone":"AH07U01","position":"H12","is_shared":false,"places_shared":0,"price":72000,"price_shared":0},{"id":4824,"status_id":1008,"status":"disponible","full_position":"AH07U01H13","zone":"AH07U01","position":"H13","is_shared":false,"places_shared":0,"price":72000,"price_shared":0},{"id":4825,"status_id":1008,"status":"disponible","full_position":"AH07U01H14","zone":"AH07U01","position":"H14","is_shared":false,"places_shared":0,"price":72000,"price_shared":0},{"id":4826,"status_id":1008,"status":"disponible","full_position":"AH07U01H15","zone":"AH07U01","position":"H15","is_shared":false,"places_shared":0,"price":72000,"price_shared":0},{"id":4827,"status_id":1008,"status":"disponible","full_position":"AH07U01H16","zone":"AH07U01","position":"H16","is_shared":false,"places_shared":0,"price":72000,"price_shared":0},{"id":4828,"status_id":1008,"status":"disponible","full_position":"AH07U01H17","zone":"AH07U01","position":"H17","is_shared":false,"places_shared":0,"price":72000,"price_shared":0}]';
                    
                    // Decodificar el JSON en un array asociativo
                    $positions = json_decode($json_response, true);

                    // Obtener el número máximo de columnas a partir de las posiciones disponibles
                    $maxColumns = 0;
                    foreach ($positions as $pos) {
                        $number = intval(substr($pos['position'], 1));
                        if ($number > $maxColumns) {
                            $maxColumns = $number;
                        }
                    }

                    // Función para obtener el nombre de posición
                    function getPositionName($pos) {
                        return $pos['position'];
                    }
                    ?>

                    <!-- Crear las columnas en el encabezado -->
                    <?php for ($j = 1; $j <= $maxColumns; $j++): ?>
                        <th><?= $j ?></th>
                    <?php endfor; ?>
                </tr>
            </thead>
            <tbody>
                <?php 
                // Obtener todas las letras distintas de las posiciones disponibles
                $letters = array_unique(array_map(function($position) {
                    return substr($position['position'], 0, 1);
                }, $positions));

                // Ordenar las letras alfabéticamente
                sort($letters);
                ?>

                <?php foreach ($letters as $letter): ?>
                    <tr>
                        <td><?= $letter ?></td>
                        <?php for ($k = 1; $k <= $maxColumns; $k++): ?>
                            <?php 
                            $positionFound = false;
                            foreach ($positions as $pos) {
                                if ($pos['position'] == $letter . $k) {
                                    $positionFound = true;
                                    break;
                                }
                            }
                            ?>
                            <?php if ($positionFound): ?>
                                <?php if ($pos['status'] == 'disponible'): ?>
                                    <td class="disponible" data-position="<?= $pos['full_position'] ?>">
                                        <?= getPositionName($pos) ?>
                                        <br>
                                        <span class="position-name"><?= $pos['full_position'] ?></span>
                                    </td>
                                <?php else: ?>
                                    <td data-position="<?= $pos['full_position'] ?>">
                                        <?= getPositionName($pos) ?>
                                        <br>
                                        <span class="position-name"><?= $pos['full_position'] ?></span>
                                    </td>
                                <?php endif; ?>
                            <?php else: ?>
                                <td></td>
                            <?php endif; ?>
                        <?php endfor; ?>
                    </tr>
                <?php endforeach; ?>
            </tbody>
        </table>
    </div>

    <script>
        // Script para manejar clics en celdas de posición
        document.querySelectorAll('td').forEach(cell => {
            cell.addEventListener('click', function() {
                // Obtener el nombre completo de la posición desde el atributo data
                const positionName = this.dataset.position;
                alert('Nombre completo de la posición: ' + positionName);
            });
        });
    </script>
